import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [sweets, setSweets] = useState([]);
  const [user, setUser] = useState(null);
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [search, setSearch] = useState('');
  const [newSweet, setNewSweet] = useState({
    name: '',
    category: '',
    price: '',
    quantity: ''
  });
  const [purchaseHistory, setPurchaseHistory] = useState(null);

  // ================= LOAD DATA =================
  useEffect(() => {
    fetchSweets();

    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      const parsedUser = JSON.parse(savedUser);
      setUser(parsedUser);
      fetchPurchaseHistory(parsedUser.id);
    }
  }, []);

  // ================= FETCH SWEETS =================
  const fetchSweets = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/sweets');
      setSweets(response.data.sweets); // ‚úÖ Now matches backend
    } catch (error) {
      console.error('Error fetching sweets:', error);
    }
  };

  // ================= FETCH PURCHASE HISTORY =================
  const fetchPurchaseHistory = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/purchases/user/${userId}`);
      setPurchaseHistory(response.data);
    } catch (error) {
      console.error('Error fetching purchase history:', error);
    }
  };

  // ================= LOGIN =================
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://localhost:5000/api/auth/login',
        { email, password }
      );
      const userData = response.data.user;
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      fetchPurchaseHistory(userData.id);
      alert('Login successful!');
    } catch (error) {
      alert('Login failed: ' + (error.response?.data?.error || error.message));
    }
  };

  // ================= REGISTER =================
  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://localhost:5000/api/auth/register',
        { email, password }
      );
      const userData = response.data.user;
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      fetchPurchaseHistory(userData.id);
      alert('Registration successful!');
      setIsLogin(true);
    } catch (error) {
      alert('Registration failed: ' + (error.response?.data?.error || error.message));
    }
  };

  // ================= PURCHASE SWEET =================
  const handlePurchase = async (id) => {
    try {
      if (!user || !user.id) {
        alert('Please login first!');
        return;
      }

      const response = await axios.post(
        `http://localhost:5000/api/sweets/${id}/purchase`,
        {
          user_id: user.id,
          quantity: 1
        }
      );

      const total = response.data.purchaseDetails.totalPrice;
      alert(`‚úÖ Purchase successful!\nSweet: ${response.data.purchaseDetails.sweetName}\nTotal Price: $${total}`);
      
      fetchSweets(); // Refresh sweets list
      fetchPurchaseHistory(user.id); // Refresh purchase history
    } catch (error) {
      alert('Purchase failed: ' + (error.response?.data?.error || error.message));
    }
  };

  // ================= ADD SWEET (ADMIN) =================
  const handleAddSweet = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/sweets', newSweet);
      setNewSweet({ name: '', category: '', price: '', quantity: '' });
      fetchSweets();
      alert('Sweet added!');
    } catch (error) {
      alert('Failed to add sweet: ' + (error.response?.data?.error || error.message));
    }
  };

  // ================= DELETE SWEET =================
  const handleDelete = async (id) => {
    if (!window.confirm('Delete this sweet?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/sweets/${id}`);
      fetchSweets();
      alert('Sweet deleted!');
    } catch (error) {
      alert('Delete failed: ' + (error.response?.data?.error || error.message));
    }
  };

  // ================= RESTOCK =================
  const handleRestock = async (id) => {
    try {
      await axios.post(`http://localhost:5000/api/sweets/${id}/restock`);
      fetchSweets();
      alert('Restocked!');
    } catch (error) {
      alert('Restock failed: ' + (error.response?.data?.error || error.message));
    }
  };

  // ================= SEARCH =================
  const handleSearch = async () => {
    if (!search.trim()) {
      fetchSweets();
      return;
    }
    
    try {
      const response = await axios.get(
        `http://localhost:5000/api/sweets/search?name=${search}`
      );
      setSweets(response.data.sweets); // ‚úÖ Now matches backend
    } catch (error) {
      console.error('Search failed:', error);
    }
  };

  // ================= LOGOUT =================
  const handleLogout = () => {
    setUser(null);
    setPurchaseHistory(null);
    localStorage.removeItem('user');
    alert('Logged out!');
  };

  // ================= FILTER =================
  const filteredSweets = sweets.filter(
    (sweet) =>
      sweet.name.toLowerCase().includes(search.toLowerCase()) ||
      sweet.category.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container">
      <header>
        <h1>üç¨ Sweet Shop Management System</h1>
        {user && (
          <div className="user-info">
            <p>Welcome, <strong>{user.email}</strong> {user.isAdmin ? '(Admin)' : '(Customer)'}</p>
            {purchaseHistory && (
              <div className="purchase-summary">
                <small>Total Purchases: {purchaseHistory.summary.purchaseCount}</small>
                <small>Total Spent: ${purchaseHistory.summary.totalAmount}</small>
              </div>
            )}
          </div>
        )}
      </header>

      {!user ? (
        <div className="auth-section">
          <h2>{isLogin ? 'Login' : 'Register'}</h2>
          <form onSubmit={isLogin ? handleLogin : handleRegister}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
          </form>
          <button onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? 'Need an account? Register' : 'Already have an account? Login'}
          </button>
          <div className="demo-credentials">
            <p><strong>Demo Accounts:</strong></p>
            <p>Admin: admin@shop.com / admin123</p>
            <p>User: user@shop.com / user123</p>
          </div>
        </div>
      ) : (
        <>
          <div className="search-bar">
            <input
              placeholder="Search sweets by name or category..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyUp={(e) => e.key === 'Enter' && handleSearch()}
            />
            <button onClick={handleSearch}>Search</button>
            <button onClick={() => { setSearch(''); fetchSweets(); }}>
              Clear
            </button>
            {purchaseHistory && (
              <button onClick={() => fetchPurchaseHistory(user.id)}>
                Refresh History
              </button>
            )}
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </div>

          {/* Purchase History Section */}
          {purchaseHistory && purchaseHistory.purchases.length > 0 && (
            <div className="purchase-history">
              <h2>üìã Your Purchase History</h2>
              <div className="history-summary">
                <p>Total Items: {purchaseHistory.summary.totalItems}</p>
                <p>Total Amount: ${purchaseHistory.summary.totalAmount}</p>
                <p>Number of Purchases: {purchaseHistory.summary.purchaseCount}</p>
              </div>
              <div className="history-grid">
                {purchaseHistory.purchases.slice(0, 5).map((purchase) => (
                  <div key={purchase.id} className="purchase-card">
                    <h4>{purchase.sweet_name}</h4>
                    <p>Qty: {purchase.quantity}</p>
                    <p>Price: ${purchase.price}</p>
                    <p>Date: {purchase.date}</p>
                    <p>Total: ${(purchase.price * purchase.quantity).toFixed(2)}</p>
                  </div>
                ))}
              </div>
            </div>
          )}

          {user.isAdmin && (
            <div className="admin-panel">
              <h2>‚ûï Add New Sweet</h2>
              <form onSubmit={handleAddSweet}>
                <input
                  placeholder="Name"
                  value={newSweet.name}
                  onChange={(e) =>
                    setNewSweet({ ...newSweet, name: e.target.value })
                  }
                  required
                />
                <input
                  placeholder="Category"
                  value={newSweet.category}
                  onChange={(e) =>
                    setNewSweet({ ...newSweet, category: e.target.value })
                  }
                  required
                />
                <input
                  type="number"
                  placeholder="Price"
                  step="0.01"
                  min="0.01"
                  value={newSweet.price}
                  onChange={(e) =>
                    setNewSweet({ ...newSweet, price: e.target.value })
                  }
                  required
                />
                <input
                  type="number"
                  placeholder="Quantity"
                  min="0"
                  value={newSweet.quantity}
                  onChange={(e) =>
                    setNewSweet({ ...newSweet, quantity: e.target.value })
                  }
                  required
                />
                <button type="submit">Add Sweet</button>
              </form>
            </div>
          )}

          <div className="sweets-section">
            <h2>üç≠ Available Sweets ({filteredSweets.length})</h2>
            <div className="sweets-grid">
              {filteredSweets.map((sweet) => (
                <div key={sweet.id} className="sweet-card">
                  <h3>{sweet.name}</h3>
                  <p className="category">Category: {sweet.category}</p>
                  <p className="price">Price: ${sweet.price}</p>
                  <p className={`stock ${sweet.quantity < 10 ? 'low-stock' : ''}`}>
                    Stock: {sweet.quantity} {sweet.quantity < 10 && '‚ö†Ô∏è'}
                  </p>

                  <button
                    className="purchase-btn"
                    onClick={() => handlePurchase(sweet.id)}
                    disabled={sweet.quantity === 0}
                  >
                    {sweet.quantity === 0 ? 'Out of Stock' : `Buy for $${sweet.price}`}
                  </button>

                  {user.isAdmin && (
                    <div className="admin-buttons">
                      <button 
                        className="restock-btn"
                        onClick={() => handleRestock(sweet.id)}
                      >
                        Restock (+10)
                      </button>
                      <button 
                        className="delete-btn"
                        onClick={() => handleDelete(sweet.id)}
                      >
                        Delete
                      </button>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </>
      )}

      <footer>
        <p>Sweet Shop Management System ¬© {new Date().getFullYear()}</p>
        <p>Total Sweets Available: {sweets.length}</p>
      </footer>
    </div>
  );
}

export default App;
