import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [sweets, setSweets] = useState([]);
  const [user, setUser] = useState(null);
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [search, setSearch] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('All');
  const [newSweet, setNewSweet] = useState({ 
    name: '', 
    category: '', 
    price: '', 
    quantity: '', 
    description: '' 
  });
  const [stats, setStats] = useState(null);
  const [restockAmount, setRestockAmount] = useState(10);

  // Load sweets on start
  useEffect(() => {
    fetchSweets();
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
      if (JSON.parse(savedUser).isAdmin) {
        fetchStats();
      }
    }
  }, []);

  const fetchSweets = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/sweets');
      setSweets(response.data);
    } catch (error) {
      console.error('Error fetching sweets:', error);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get('http://localhost:5000/admin/stats');
      setStats(response.data.stats);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });
      setUser(response.data.user);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      
      if (response.data.user.isAdmin) {
        fetchStats();
      }
      
      alert('Login successful!');
    } catch (error) {
      alert('Login failed: ' + (error.response?.data?.error || error.message));
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/register', { email, password });
      setUser(response.data.user);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      alert('Registration successful!');
      setIsLogin(true);
    } catch (error) {
      alert('Registration failed: ' + (error.response?.data?.error || error.message));
    }
  };

  const handlePurchase = async (id) => {
    try {
      if (!user || !user.id) {
        alert('Please login first!');
        return;
      }
      
      await axios.post(`http://localhost:5000/api/sweets/${id}/purchase`, {
        user_id: user.id
      });
      
      fetchSweets();
      if (user.isAdmin) fetchStats();
      alert('Purchase successful!');
    } catch (error) {
      alert('Purchase failed: ' + (error.response?.data?.error || error.message));
    }
  };

  const handleAddSweet = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/sweets', newSweet);
      setNewSweet({ name: '', category: '', price: '', quantity: '', description: '' });
      fetchSweets();
      fetchStats();
      alert('Sweet added successfully!');
    } catch (error) {
      alert('Failed to add sweet: ' + error.response?.data?.error);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this sweet?')) {
      try {
        await axios.delete(`http://localhost:5000/api/sweets/${id}`);
        fetchSweets();
        fetchStats();
        alert('Sweet deleted!');
      } catch (error) {
        alert('Failed to delete');
      }
    }
  };

  const handleRestock = async (id) => {
    try {
      await axios.post(`http://localhost:5000/api/sweets/${id}/restock`, {
        amount: parseInt(restockAmount)
      });
      fetchSweets();
      fetchStats();
      alert(`Restocked ${restockAmount} items!`);
    } catch (error) {
      alert('Restock failed: ' + error.response?.data?.error);
    }
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/sweets/search?name=${search}&category=${categoryFilter}`);
      setSweets(response.data);
    } catch (error) {
      console.error('Search failed');
    }
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
    setStats(null);
    alert('Logged out!');
  };

  // Get unique categories
  const categories = ['All', ...new Set(sweets.map(sweet => sweet.category))];

  // Filter sweets based on search and category
  const filteredSweets = sweets.filter(sweet => {
    const matchesSearch = sweet.name.toLowerCase().includes(search.toLowerCase()) ||
                         sweet.description?.toLowerCase().includes(search.toLowerCase());
    const matchesCategory = categoryFilter === 'All' || sweet.category === categoryFilter;
    return matchesSearch && matchesCategory;
  });

  return (
    <div className="container">
      {/* Header */}
      <header>
        <h1>üç¨ Sweet Shop Management System</h1>
        {user && (
          <div className="user-info">
            <p>Welcome, <strong>{user.email}</strong> {user.isAdmin ? '(Admin)' : '(Customer)'}</p>
            <small>User ID: {user.id}</small>
          </div>
        )}
      </header>

      {/* Login/Register Section */}
      {!user ? (
        <div className="auth-section">
          <h2>{isLogin ? 'Login' : 'Register'}</h2>
          <form onSubmit={isLogin ? handleLogin : handleRegister}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
          </form>
          <button onClick={() => setIsLogin(!isLogin)} className="switch-btn">
            {isLogin ? 'Need an account? Register' : 'Already have account? Login'}
          </button>
          <div className="test-accounts">
            <h4>Test Accounts:</h4>
            <p>Admin: admin@shop.com / admin123</p>
            <p>User: user@shop.com / user123</p>
          </div>
        </div>
      ) : (
        <>
          {/* Admin Stats */}
          {user.isAdmin && stats && (
            <div className="admin-stats">
              <h3>üìä Shop Statistics</h3>
              <div className="stats-grid">
                <div className="stat-card">
                  <h4>Total Users</h4>
                  <p>{stats.totalUsers}</p>
                </div>
                <div className="stat-card">
                  <h4>Total Sweets</h4>
                  <p>{stats.totalSweets}</p>
                </div>
                <div className="stat-card">
                  <h4>Total Stock</h4>
                  <p>{stats.totalStock}</p>
                </div>
                <div className="stat-card">
                  <h4>Total Purchases</h4>
                  <p>{stats.totalPurchases}</p>
                </div>
                <div className="stat-card">
                  <h4>Total Revenue</h4>
                  <p>${stats.totalRevenue?.toFixed(2) || '0.00'}</p>
                </div>
              </div>
            </div>
          )}

          {/* Search & Filter Bar */}
          <div className="search-filter-bar">
            <div className="search-box">
              <input
                type="text"
                placeholder="Search sweets by name or description..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                onKeyUp={handleSearch}
              />
              <button onClick={handleSearch}>üîç Search</button>
            </div>
            
            <div className="filter-box">
              <select value={categoryFilter} onChange={(e) => setCategoryFilter(e.target.value)}>
                {categories.map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
              <button onClick={() => {setSearch(''); setCategoryFilter('All'); fetchSweets();}}>
                Clear Filters
              </button>
            </div>
            
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </div>

          {/* Admin Panel */}
          {user.isAdmin && (
            <div className="admin-panel">
              <h2>‚ûï Add New Sweet</h2>
              <form onSubmit={handleAddSweet}>
                <div className="form-row">
                  <input
                    placeholder="Sweet Name"
                    value={newSweet.name}
                    onChange={(e) => setNewSweet({...newSweet, name: e.target.value})}
                    required
                  />
                  <input
                    placeholder="Category (e.g., Chocolate, Candy, Bakery)"
                    value={newSweet.category}
                    onChange={(e) => setNewSweet({...newSweet, category: e.target.value})}
                    required
                  />
                </div>
                <div className="form-row">
                  <input
                    type="number"
                    placeholder="Price"
                    value={newSweet.price}
                    onChange={(e) => setNewSweet({...newSweet, price: e.target.value})}
                    step="0.01"
                    required
                  />
                  <input
                    type="number"
                    placeholder="Quantity"
                    value={newSweet.quantity}
                    onChange={(e) => setNewSweet({...newSweet, quantity: e.target.value})}
                    required
                  />
                </div>
                <input
                  placeholder="Description"
                  value={newSweet.description}
                  onChange={(e) => setNewSweet({...newSweet, description: e.target.value})}
                  className="full-width"
                />
                <button type="submit">Add Sweet</button>
              </form>
              
              <div className="restock-control">
                <h4>Restock Control</h4>
                <div className="restock-input">
                  <input
                    type="number"
                    placeholder="Restock amount"
                    value={restockAmount}
                    onChange={(e) => setRestockAmount(e.target.value)}
                    min="1"
                  />
                  <small>Default: 10 items per restock</small>
                </div>
              </div>
            </div>
          )}

          {/* Sweets Grid */}
          <div className="sweets-grid">
            <h2>üç≠ Available Sweets ({filteredSweets.length})</h2>
            {filteredSweets.length === 0 ? (
              <p className="no-sweets">No sweets found. Try a different search or add some sweets!</p>
            ) : (
              <div className="grid">
                {filteredSweets.map(sweet => (
                  <div key={sweet.id} className="sweet-card">
                    <div className="sweet-header">
                      <h3>{sweet.name}</h3>
                      <span className="category-badge">{sweet.category}</span>
                    </div>
                    <p className="description">{sweet.description || 'Delicious sweet treat'}</p>
                    <div className="sweet-details">
                      <p className="price">üí∞ ${sweet.price}</p>
                      <p className={`quantity ${sweet.quantity === 0 ? 'out-of-stock' : 
                                       sweet.quantity < 10 ? 'low-stock' : 'in-stock'}`}>
                        üì¶ Stock: {sweet.quantity} units
                      </p>
                    </div>
                    
                    <div className="actions">
                      <button
                        onClick={() => handlePurchase(sweet.id)}
                        disabled={sweet.quantity === 0}
                        className={`purchase-btn ${sweet.quantity === 0 ? 'disabled' : ''}`}
                      >
                        {sweet.quantity === 0 ? 'Out of Stock' : 'üõí Purchase Now'}
                      </button>
                      
                      {user.isAdmin && (
                        <div className="admin-actions">
                          <div className="restock-action">
                            <button onClick={() => handleRestock(sweet.id)} className="restock-btn">
                              üì¶ Restock ({restockAmount})
                            </button>
                          </div>
                          <button onClick={() => handleDelete(sweet.id)} className="delete-btn">
                            üóëÔ∏è Delete
                          </button>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </>
      )}

      {/* Footer */}
      <footer>
        <p>Sweet Shop Management System | üç¨ {sweets.length} Sweets Available | SQLite Database</p>
        <small>Backend: localhost:5000 | Frontend: localhost:3000</small>
      </footer>
    </div>
  );
}

export default App;
