import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [sweets, setSweets] = useState([]);
  const [user, setUser] = useState(null);
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [search, setSearch] = useState('');
  const [newSweet, setNewSweet] = useState({ name: '', category: '', price: '', quantity: '' });

  // Load sweets on start
  useEffect(() => {
    fetchSweets();
    // Load user from localStorage if exists
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  const fetchSweets = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/sweets');
      setSweets(response.data);
    } catch (error) {
      console.error('Error fetching sweets:', error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });
      setUser(response.data.user);
      // Save user to localStorage
      localStorage.setItem('user', JSON.stringify(response.data.user));
      alert('Login successful!');
    } catch (error) {
      alert('Login failed: ' + (error.response?.data?.error || error.message));
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/register', { email, password });
      setUser(response.data.user);
      // Save user to localStorage
      localStorage.setItem('user', JSON.stringify(response.data.user));
      alert('Registration successful!');
      setIsLogin(true);
    } catch (error) {
      alert('Registration failed: ' + (error.response?.data?.error || error.message));
    }
  };

  // ‚úÖ FIXED: Purchase function now sends user_id
  const handlePurchase = async (id) => {
    try {
      if (!user || !user.id) {
        alert('Please login first!');
        return;
      }
      
      console.log('Purchasing with user ID:', user.id); // Debug
      
      await axios.post(`http://localhost:5000/api/sweets/${id}/purchase`, {
        user_id: user.id  // ‚úÖ Send user_id in request body
      });
      
      fetchSweets(); // Refresh list
      alert('Purchase successful!');
    } catch (error) {
      alert('Purchase failed: ' + (error.response?.data?.error || error.message));
    }
  };

  const handleAddSweet = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/sweets', newSweet);
      setNewSweet({ name: '', category: '', price: '', quantity: '' });
      fetchSweets();
      alert('Sweet added!');
    } catch (error) {
      alert('Failed to add sweet: ' + error.response?.data?.error);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Delete this sweet?')) {
      try {
        await axios.delete(`http://localhost:5000/api/sweets/${id}`);
        fetchSweets();
        alert('Sweet deleted!');
      } catch (error) {
        alert('Failed to delete');
      }
    }
  };

  const handleRestock = async (id) => {
    try {
      await axios.post(`http://localhost:5000/api/sweets/${id}/restock`);
      fetchSweets();
      alert('Restocked!');
    } catch (error) {
      alert('Restock failed');
    }
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/sweets/search?name=${search}`);
      setSweets(response.data);
    } catch (error) {
      console.error('Search failed');
    }
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
    alert('Logged out!');
  };

  // Filter sweets based on search
  const filteredSweets = sweets.filter(sweet =>
    sweet.name.toLowerCase().includes(search.toLowerCase()) ||
    sweet.category.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container">
      {/* Header */}
      <header>
        <h1>üç¨ Sweet Shop Management System</h1>
        {user && <p>Welcome, {user.email} {user.isAdmin ? '(Admin)' : ''} (ID: {user.id})</p>}
      </header>

      {/* Login/Register Section */}
      {!user ? (
        <div className="auth-section">
          <h2>{isLogin ? 'Login' : 'Register'}</h2>
          <form onSubmit={isLogin ? handleLogin : handleRegister}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
          </form>
          <button onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? 'Need an account? Register' : 'Already have account? Login'}
          </button>
          <p style={{marginTop: '20px', color: '#666'}}>
            <strong>Test Accounts:</strong><br/>
            Admin: admin@shop.com / admin123<br/>
            User: user@shop.com / user123
          </p>
        </div>
      ) : (
        <>
          {/* Search Bar */}
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search sweets by name or category..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyUp={handleSearch}
            />
            <button onClick={() => {setSearch(''); fetchSweets();}}>Clear</button>
            <button onClick={handleLogout} style={{background: '#ff4444'}}>Logout</button>
          </div>

          {/* Admin Panel */}
          {user.isAdmin && (
            <div className="admin-panel">
              <h2>Admin Panel - Add New Sweet</h2>
              <form onSubmit={handleAddSweet}>
                <input
                  placeholder="Sweet Name"
                  value={newSweet.name}
                  onChange={(e) => setNewSweet({...newSweet, name: e.target.value})}
                  required
                />
                <input
                  placeholder="Category"
                  value={newSweet.category}
                  onChange={(e) => setNewSweet({...newSweet, category: e.target.value})}
                  required
                />
                <input
                  type="number"
                  placeholder="Price"
                  value={newSweet.price}
                  onChange={(e) => setNewSweet({...newSweet, price: e.target.value})}
                  step="0.01"
                  required
                />
                <input
                  type="number"
                  placeholder="Quantity"
                  value={newSweet.quantity}
                  onChange={(e) => setNewSweet({...newSweet, quantity: e.target.value})}
                  required
                />
                <button type="submit">Add Sweet</button>
              </form>
            </div>
          )}

          {/* Sweets Grid */}
          <div className="sweets-grid">
            <h2>Available Sweets ({filteredSweets.length})</h2>
            <div className="grid">
              {filteredSweets.map(sweet => (
                <div key={sweet.id} className="sweet-card">
                  <h3>{sweet.name}</h3>
                  <p className="category">Category: {sweet.category}</p>
                  <p className="price">Price: ${sweet.price}</p>
                  <p className={`quantity ${sweet.quantity === 0 ? 'out-of-stock' : ''}`}>
                    Stock: {sweet.quantity} units
                  </p>
                  
                  <div className="actions">
                    <button
                      onClick={() => handlePurchase(sweet.id)}
                      disabled={sweet.quantity === 0}
                      className={sweet.quantity === 0 ? 'disabled' : ''}
                    >
                      {sweet.quantity === 0 ? 'Out of Stock' : 'üõí Purchase'}
                    </button>
                    
                    {user.isAdmin && (
                      <>
                        <button onClick={() => handleRestock(sweet.id)} className="restock">
                          üì¶ Restock
                        </button>
                        <button onClick={() => handleDelete(sweet.id)} className="delete">
                          üóëÔ∏è Delete
                        </button>
                      </>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </>
      )}

      {/* Footer */}
      <footer>
        <p>Sweet Shop Management System | SQLite Database | All features working</p>
      </footer>
    </div>
  );
